LIBRARY IEEE;
USE IEEE.STD_LOGIC_1164.ALL;
USE ieee.numeric_std.ALL;

ENTITY top_VGAbuffer IS
    PORT (
        clk : IN STD_LOGIC;
        btnR : IN STD_LOGIC;
        -- sw : IN STD_LOGIC_VECTOR(15 DOWNTO 0);
        Hsync : OUT STD_LOGIC;
        Vsync : OUT STD_LOGIC;
        vgaRed : OUT STD_LOGIC_VECTOR(3 DOWNTO 0);
        vgaGreen : OUT STD_LOGIC_VECTOR(3 DOWNTO 0);
        vgaBlue : OUT STD_LOGIC_VECTOR(3 DOWNTO 0)
    );
END top_VGAbuffer;

ARCHITECTURE Behavioral OF top_VGAbuffer IS

    -- Signals
    SIGNAL clk_pix : STD_LOGIC; -- pixel clock (25 MHz)
    SIGNAL clk_lines : STD_LOGIC; -- line clock for vsynch

    SIGNAL hdisplay : STD_LOGIC; -- active low for horizontal display
    SIGNAL vdisplay : STD_LOGIC; -- active low for vertical display

    SIGNAL hpixel_count : INTEGER; -- Index of current hozizontal pixel
    SIGNAL vpixel_count : INTEGER; -- Index of current vertival pixel

    SIGNAL write_pix : STD_LOGIC_VECTOR(11 DOWNTO 0) := "000011110000"; -- Stores the current read pixel data
    SIGNAL read_pix : STD_LOGIC_VECTOR(11 DOWNTO 0); -- Reads current pix value

    SIGNAL bit_map : STD_LOGIC_VECTOR(0 TO 65535) := "";
    SIGNAL pix_index : INTEGER := 0;
    SIGNAL test_bit : STD_LOGIC;

    -- Components
    COMPONENT hsynch
        PORT (
            clk_pix_in : IN STD_LOGIC;
            rst_in : IN STD_LOGIC;
            hline_end_out : OUT STD_LOGIC;
            hsynch_out : OUT STD_LOGIC;
            hdisplay_out : OUT STD_LOGIC;
            hpixel_count_out : OUT INTEGER
        );
    END COMPONENT hsynch;

    COMPONENT vsynch
        PORT (
            clk_pix_in : IN STD_LOGIC;
            rst_in : IN STD_LOGIC;
            vsynch_out : OUT STD_LOGIC;
            vdisplay_out : OUT STD_LOGIC;
            vpixel_count_out : OUT INTEGER
        );
    END COMPONENT vsynch;

    COMPONENT gen_pixel_clk
        PORT (
            clk_in1 : IN STD_LOGIC;
            reset : IN STD_LOGIC;
            clk_pix_out : OUT STD_LOGIC
        );
    END COMPONENT;
BEGIN
  
    -- Inst MMCM fir pixel clock 25 MHz
    clock_gen_isnt : gen_pixel_clk
    PORT MAP(
        clk_in1 => clk,
        clk_pix_out => clk_pix,
        reset => btnR
    );

    -- Instantiate the hsynch component
    HSYNCH_inst : hsynch
    PORT MAP(
        clk_pix_in => clk_pix,
        rst_in => btnR,
        hline_end_out => clk_lines,
        hsynch_out => Hsync,
        hdisplay_out => hdisplay,
        hpixel_count_out => hpixel_count
    );

    -- Instantiate the vsynch component
    VSYNCH_inst : vsynch
    PORT MAP(
        clk_pix_in => clk_lines,
        rst_in => btnR,
        vsynch_out => Vsync,
        vdisplay_out => vdisplay,
        vpixel_count_out => vpixel_count
    );

    -- output_process : PROCESS (ALL)
    -- BEGIN
    --     IF (hdisplay = '0' AND vdisplay = '0') THEN
    --         vgaRed <= sw(0) & sw(1) & sw(2) & sw(3);
    --         vgaGreen <= sw(4) & sw(5) & sw(6) & sw(7);
    --         vgaBlue <= sw(8) & sw(9) & sw(10) & sw(11);
    --     ELSE
    --         vgaRed <= (OTHERS => '0');
    --         vgaGreen <= (OTHERS => '0');
    --         vgaBlue <= (OTHERS => '0');
    --     END IF;
    -- END PROCESS;
    output_process : PROCESS (ALL)
    BEGIN
        IF hpixel_count <= 127 AND vpixel_count <= 63 THEN
            pix_index <= 128 * 8 * vpixel_count + 8 * hpixel_count;
            vgaRed <= bit_map(pix_index) & bit_map(pix_index + 1) & '0' & '0';
            vgaGreen <= bit_map(pix_index + 2) & bit_map(pix_index + 3) & '0' & '0';
            vgaBlue <= bit_map(pix_index + 4) & bit_map(pix_index + 5) & '0' & '0';

        ELSE
            pix_index <= 0;
            vgaRed <= (OTHERS => '0');
            vgaGreen <= (OTHERS => '0');
            vgaBlue <= (OTHERS => '0');
        END IF;
    END PROCESS output_process;

END Behavioral;